fn toggle_workspace(&mut self, workspace_id: String, cx: &mut Context<Self>) {
    if let Some(workspace) = self.workspaces.iter_mut().find(|w| w.id == workspace_id) {
        workspace.is_expanded = !workspace.is_expanded;
        cx.notify();
    }
}

